        $total_videos = $conn->query("SELECT COUNT(*) FROM media WHERE user_id = $user_id AND type = 'video'")->fetch_row()[0];

        // Buscar mídias do usuário
        $media_query = "SELECT * FROM media WHERE user_id = ? ORDER BY upload_date DESC";
        $stmt = $conn->prepare($media_query);
        $stmt->bind_param("i", $user_id);
        $stmt->execute();
        $result = $stmt->get_result();
